!--------------------------G-S method-----------------------------------------------
subroutine LyaExp(ynext,znorm)
use problemsize
implicit none
integer i,j,k
real*8 ::ynext(nn),znorm(n),gsc(n) 
    znorm(1)=0.0d0
    do i=1,n
      znorm(1)=znorm(1)+abs(ynext(n*i+1))**2.0d0
    end do 
    znorm(1)=sqrt(znorm(1))
    !write(*,*) znorm(1)
    do i=1,n
      ynext(n*i+1)=ynext(n*i+1)/znorm(1)
    end do

    do i=2,n
      do j=1,i-1
        gsc(j)=0.0d0
        do k=1,n
          gsc(j)=gsc(j)+ynext(n*k+i)*ynext(n*k+j)
        end do
      end do
      do j=1,n
        do k=1,i-1
          ynext(n*j+i)=ynext(n*j+i)-gsc(k)*ynext(n*j+k)
        end do
      end do
      znorm(i)=0.0d0
      do j=1,n
        znorm(i)=znorm(i)+abs(ynext(n*j+i))**2.0d0
      end do
      znorm(i)=sqrt(znorm(i))
      do j=1,n
        ynext(n*j+i)=ynext(n*j+i)/znorm(i)
      end do
    end do
 return
end

